window.onscroll=function(){let e=document.querySelector(".header"),t=document.querySelector(".content");window.pageYOffset>0?(e.classList.add("__fixed"),t.classList.add("__fixed")):(e.classList.remove("__fixed"),t.classList.remove("__fixed"))};let openMenuBtn=document.querySelector(".header__burger"),closeMenuBtn=document.querySelector(".header__close-menu");if(openMenuBtn){const e=document.querySelector(".header__menu");openMenuBtn.addEventListener("click",(function(){e.classList.add("__active"),document.querySelector("body").classList.add("__noscroll")}))}const menuActive=document.querySelector(".header__menu");closeMenuBtn&&closeMenuBtn.addEventListener("click",(function(){menuActive.classList.remove("__active"),document.querySelector("body").classList.remove("__noscroll")})),menuActive.addEventListener("click",(function(e){e.target.closest(".header__menu-body")||(menuActive.classList.remove("__active"),document.querySelector("body").classList.remove("__noscroll"))})),document.addEventListener("keydown",(e=>{"Escape"===e.key&&(menuActive.classList.remove("__active"),document.querySelector("body").classList.remove("__noscroll"))}));